import React, { useEffect } from "react";
import { useState } from "react";

const SplashScreen = ({ onLoaded }) => {
  const [fadeIn, setFadeIn] = useState(false);
  const [fadeOut, setFadeOut] = useState(false);
  const [progress, setProgress] = useState(0);

  useEffect(() => {

    const progressInterval = setInterval(() => {
      setProgress((prev) => Math.min(prev + 10, 100));
    }, 100);

    const fadeInTimer = setTimeout(() => {
      setFadeIn(true);
    });

    const fadeOutTimer = setTimeout(() => {
      setFadeOut(true);
      setTimeout(onLoaded, 300);
      clearInterval(progressInterval);
    }, 1200);
    
    return () => {
      clearTimeout(fadeInTimer);
      clearTimeout(fadeOutTimer);
      clearInterval(progressInterval);
    };
  }, [onLoaded]);

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100vh",
        backgroundColor: "#fff",
        opacity: fadeOut ? 0 : fadeIn ? 1 : 0,
        transition: "opacity 1s ease-in-out",
      }}
      className="flex flex-col items-center"
    >
      
      <img src={`data:image/png;base64,`}
        alt="App Logo" style={{ width: 200, height: 200 }} />
      
      <h1 className="text-2xl font-semibold">
        All-in-One Paint Solution
      </h1>

      <div style={{ width: "280px", height: "3px" }} className=" mt-2 bg-gray-300 rounded overflow-hidden mb-4">
        <div
          style={{ width: `${progress}%` }} // Use progress state here
          className="h-full bg-purple-900 transition-all ease-linear"
        ></div>
      </div>
    </div>
  );
};

export default SplashScreen;
